import React from "react";
import { evaluate } from 'mathjs';

export class Calculator extends React.Component{

    constructor(props){
        super(props);

        this.state = {
            calculate : '0',
            display: '0',
            equals: false
        };

        this.changeCalculate = this.changeCalculate.bind(this);
        this.sumCalculate = this.sumCalculate.bind(this);
        this.clearCalculate = this.clearCalculate.bind(this);
        this.changeCharge = this.changeCharge.bind(this);

    }


    changeCalculate(e){

        // if this input is not after an equals sign
        this.setState((prevState) => {
            let newDisplayValue = this.getDisplayValue(e);
            const newCalcValue = e.target.value;

            const calcCount = prevState.calculate.length; // calculate state count
            const lastLetter = prevState.calculate.slice(-1); // last letter of the calc state

            const descCount = prevState.display.length; // display state count

            // if equals was the last button clicked
            if(this.state.equals == false){

                // if it's the first input and the number is bigger than zero
                // if both previous and new input are not numbers
                if( calcCount == 1 && prevState.calculate == '0' && parseFloat(e.target.value) > 0 || isNaN(parseFloat(lastLetter)) && isNaN((parseFloat(newCalcValue)))) {

                    // replace the previous input
                    return ({ 
                        calculate: prevState.calculate.slice(0, calcCount -1) + newCalcValue,
                        display: prevState.display.slice(0, descCount -1) + newDisplayValue,
                    });

                }else{

                    // don't replace
                    return ({ 
                        calculate: prevState.calculate + newCalcValue,
                        display: prevState.display + newDisplayValue,
                    });
                }

            }else{            
                // if new value is not a number
                if(isNaN(newCalcValue)){

                    // add to previous values
                    return ({ 
                        calculate: prevState.calculate + newCalcValue,
                        display: prevState.display + newDisplayValue,
                    });
                }else{

                    // replace the previous value entirely
                    return ({ 
                        calculate: newCalcValue,
                        display: newDisplayValue
                    });
                }
            }

        });

        this.clearEquals();
    }

    changeCharge(e){
        this.setState((prevState) => {
            console.log('--------------------------------------------');
            // const lastLetter = prevState.calculate.slice(-3); // last letter of the calc state

            const calcCount = prevState.calculate.length; // calculate state count
            const descCount = prevState.display.length; // display state count


            const splitString = prevState.calculate.match(/(?:\d+\.)?\d+/g); // split by numbers
            const lastItem = splitString[splitString.length - 1]; // get the last number

            const letterCount = lastItem.length; // char count of the last number
            const lastChar = prevState.calculate.charAt( lastItem.length); // the last char
            const beforeLast = prevState.calculate.charAt( lastItem.length - 1);

            console.log('last item: ' + lastItem);
            console.log('letters: ' +  letterCount);
            console.log('last characters: ' + lastChar);
            console.log('before last characters: ' + beforeLast);


            // if there's only one number
            if( splitString.length == 1 ){
                console.log('one item in array');

                if(prevState.calculate.charAt( 0) == '-'){
                    console.log('remove -');
                    return ({
                        calculate: prevState.calculate.substr(1),
                        display: prevState.display.substr(1)
                    });
                }else{
                    return ({
                        calculate: '-' + prevState.calculate,
                        display: '-' + prevState.display
                    });
                }
            }else{
                console.log('more than one item in array');

                const newLastNumber = '-' + lastItem;

                if(lastChar == '-' && beforeLast == '-'){
                    // return ({
                    //     calculate: prevState.calculate.substr(-letterCount - 1) + newLastNumber,
                    //     display: prevState.display.substr(-letterCount - 1) + newLastNumber
                    // });
                }else{
                    return ({
                        calculate: prevState.calculate.substr(0, letterCount - 1) + newLastNumber,
                        display: prevState.display.substr(0, letterCount - 1) + newLastNumber
                    });
                }

                // if()
                // return ({
                //     calculate: '-' + prevState.calculate.substr(lastItem.length - 1),
                //     display: '-' + prevState.display.substr(lastItem.length - 1)
                // });

            }





            // if there's only one number
            // if( calcCount == 1 ){

            //     if(!isNaN(prevState.calculate)){
            //         return ({
            //             calculate: '-' + prevState.calculate,
            //             display: '-' + prevState.display
            //         });
            //     }

            // } else if(calcCount == 2){
            //     console.log('calcount is 2');

            //     if(lastLetter.charAt(0) == '-' && !isNaN(lastLetter.charAt(1))){
            //         // remove the minus

            //         return ({
            //             calculate: lastLetter.charAt(1),
            //             display: lastLetter.charAt(1)
            //         });

            //     }

            // }else if(calcCount >= 3){

            //     if(lastLetter.charAt(0) == '-' && lastLetter.charAt(1) == '-'){
            //         console.log('hello');
            //     }else{
            //         return ({
            //             calculate: prevState.calculate.slice(0, calcCount -1) + '-' + lastLetter.charAt(2),
            //             display: prevState.display.slice(0, descCount -1) + '-' + lastLetter.charAt(2),
            //         });
            //     }
            // }


        });
    }


    clearCalculate(e){
        this.setState(() => ({ 
            display: '0',
            calculate: '0',
        }));

        this.clearEquals();
    }

    sumCalculate(e){
        let result = '';
        result = evaluate(this.state.calculate);

        if(result != null){
            this.setState(() => ({ 
                display: result,
                equals: true
            }));
        }

    }

    clearEquals(){
        this.setState(() => ({ 
            equals: false
        }));
    }

    getDisplayValue(e) {

        let displayValue = '';

        if(e.target.getAttribute('display') !== null){
            displayValue = e.target.getAttribute('display');
        }else{
            displayValue = e.target.value;
        }

        return displayValue;

    }


