import React from "react";
import { evaluate } from 'mathjs';

export class Calculator extends React.Component{

    constructor(props){
        super(props);

        this.state = {
            calculate : '0',
            display: '0',
            equals: false
        };

        this.changeCalculate = this.changeCalculate.bind(this);
        this.sumCalculate = this.sumCalculate.bind(this);
        this.clearCalculate = this.clearCalculate.bind(this);


    }


    changeCalculate(e){
        let displayValue = '';

        if(e.target.getAttribute('display') !== null){
            displayValue = e.target.getAttribute('display');
        }else{
            displayValue = e.target.value;
        }

        // if this input is not after an equals sign
        if(this.state.equals == false){

            this.setState((prevState) => {
                // check if the last character wasn't an operation sign, if it was, switch is rather than add
                const valueCount = prevState.calculate.split.length;
                const lastLetter = prevState.calculate.slice(-1);

                console.log('value count: ' + valueCount);
                console.log(lastLetter + ' + ' + e.target.value);
                // if the original zero isn't being replaced
                // if both previous and new input are numbers

                if(valueCount == 2 && lastLetter == 0 && parseFloat(lastLetter) == 0){
                    // replace the last letter
                    console.log('false');
                }
                else if(parseFloat(lastLetter) && parseFloat(e.target.value) ){
                    // don't replace the last letter
                    console.log('dont remplace');
                }else{
                    console.log('replace');
                }


                return ({ 
                    calculate: prevState.calculate + e.target.value,
                    display: prevState.display + displayValue,
                })
            });

        }else{

            this.setState(() => ({ 
                calculate:  e.target.value,
                display: displayValue,
            }));

        }

        this.clearEquals();

        // console.log(displayValue);

    }


    clearCalculate(e){
        this.setState(() => ({ 
            display: ' ',
            calculate: ' ',
        }));

        this.clearEquals();
    }

    sumCalculate(e){
        let result = '';
        result = evaluate(this.state.calculate);

        if(result != null){
            this.setState(() => ({ 
                display: result,
                equals: true
            }));
        }

        console.log(this.state.calculate);
    }

    clearEquals(){
        this.setState(() => ({ 
            equals: false
        }));
    }


    render(){
        return(  
            <div className={'ui grid calculator'}>
                <div className={ 'row results-row '}>
                    <span>{ this.state.display }</span>
                </div>
                <div className={ 'row'}>
                    <div className={"twelve wide column"}>
                        <div className={'ui grid'}>
                            <div className={'three column row'}>
                                <div className={'column'}>
                                    <button onClick={this.clearCalculate} className={'ui button fluid'}>C</button>
                                </div>

                                <div className={'column'}>
                                    <button className={'ui button fluid'}>+/-</button>
                                </div>

                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='%' className={'ui button fluid'}>%</button>
                                </div>

                            </div>
                            <div className={'three column row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='1' className={'ui button fluid'}>1</button>
                                </div>

                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='2' className={'ui button fluid'}>2</button>
                                </div>

                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='3' className={'ui button fluid'}>3</button>
                                </div>
                            </div>

                            <div className={'three column row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='4' className={'ui button fluid'}>4</button>
                                </div>

                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='5' className={'ui button fluid'}>5</button>
                                </div>

                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='6' className={'ui button fluid'}>6</button>
                                </div>
                            </div>
                            <div className={'three column row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='7' className={'ui button fluid'}>7</button>
                                </div>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='8' className={'ui button fluid'}>8</button>
                                </div>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='9' className={'ui button fluid'}>9</button>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className={"four wide column"}>
                        <div className={'ui grid'}>
                            <div className={'row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='+' className={'ui button fluid teal'}>+</button>
                                </div>
                            </div>
                            <div className={'row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='-' className={'ui button fluid teal'}>-</button>
                                </div>
                            </div>
                            <div className={'row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='*' display='x' className={'ui button fluid teal'}>x</button>
                                    </div>
                            </div>
                            <div className={'row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='/' className={'ui button fluid teal'}>/</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                <div className={ 'row'}>
                    <div className={'four wide column'}>
                        <button onClick={this.changeCalculate} value='0' className={'ui button fluid'}>0</button>
                    </div>
                    <div className={'four wide column'}>
                        <button onClick={this.changeCalculate} value='.' className={'ui button fluid'}>.</button>
                    </div>
                    <div className={'eight wide column'}>
                        <button className={'ui button fluid grey'} onClick={this.sumCalculate} >=</button>
                    </div>

                </div>
            </div>

        );
    }
}
