import React from "react";
import { evaluate } from 'mathjs';

export class Calculator extends React.Component{

    constructor(props){
        super(props);

        this.state = {
            calculate : '0',
            display: '0',
            equals: false
        };

        this.changeCalculate = this.changeCalculate.bind(this);
        this.sumCalculate = this.sumCalculate.bind(this);
        this.clearCalculate = this.clearCalculate.bind(this);
        this.changeCharge = this.changeCharge.bind(this);

    }


    //**
    // Changes the equation value. 
    // First, it needs to be established if the last character needs to be replaced
    // eg. '+' by '-', or the first '0' by '5', so it's not '05' but just '5'
    //**
    changeCalculate(e){

        // if this input is not after an equals sign
        this.setState((prevState) => {
            const newDisplayValue = this.getDisplayValue(e);
            const newCalcValue = e.target.value;

            const calcLength = prevState.calculate.length; // calculate state count
            const lastCalcChar = prevState.calculate.slice(-1); // last letter of the calc state

            const descLength = prevState.display.length; // display state count

            // if equals was not the last button clicked
            if(this.state.equals == false){

                // if the last char was a '%' and new is a number
                if( lastCalcChar == '%' && !isNaN(newCalcValue)){

                    // ignore
                    return ({
                        calculate: prevState.calculate,
                        display: prevState.display,
                    });


                }else if(
                    // if it's the first input and the number is bigger than zero
                    calcLength == 1 && prevState.calculate == '0' && parseFloat(e.target.value) > 0 || 
                    // if both previous and new input are not numbers, or the previous character isn't a '%'
                    isNaN(parseFloat(lastCalcChar)) && isNaN((parseFloat(newCalcValue)) ) && lastCalcChar !== '%' || 
                    // if both previous and new value are not a '%'
                    lastCalcChar == '%' && newCalcValue == '%'
                ) {

                    // replace the previous input
                    return ({ 
                        calculate: prevState.calculate.slice(0, calcLength -1) + newCalcValue,
                        display: prevState.display.slice(0, descLength -1) + newDisplayValue,
                    });

                }else{

                    // add to previous
                    return ({ 
                        calculate: prevState.calculate + newCalcValue,
                        display: prevState.display + newDisplayValue,
                    });
                }

            // if it's first chara after '='
            }else{            
                // if new value is not a number
                if(isNaN(newCalcValue)){
                    // add to previous values
                    return ({ 
                        calculate: prevState.calculate + newCalcValue,
                        display: prevState.display + newDisplayValue,
                    });
                }else{

                    // replace the previous value entirely
                    return ({ 
                        calculate: newCalcValue,
                        display: newDisplayValue
                    });
                }
            }

        });

        this.clearEquals();
    }

    // Switches the last number between positive and negative
    changeCharge(e){
        this.setState((prevState) => {     
            // The whole calculation length   
            const calcLength = prevState.calculate.length; // calculate state count

            // Last Item
            const calcSplit = prevState.calculate.match(/(?:\d+\.)?\d+/g); // split by numbers
            const lastItem = calcSplit[calcSplit.length - 1]; // get the last number
            const lastItemLength = lastItem.length; // char count of the last number

            // Before items
            const beforeLast = prevState.calculate.charAt( calcLength - lastItem.length - 1);
            const beforeBeforeLast = prevState.calculate.charAt( calcLength - lastItem.length - 2);


            // if there's only one number
            if( calcSplit.length == 1 ){

                // if there's a '-' in front of that number
                if(prevState.calculate.charAt( 0) == '-'){

                    // remove the first character
                    return ({
                        calculate: prevState.calculate.substr(1),
                        display: prevState.display.substr(1)
                    });

                    // if there isn't a '-' in front of that number
                }else if(prevState.calculate != '0'){

                    // add '-' in front of the first number
                    return ({
                        calculate: '-' + prevState.calculate,
                        display: '-' + prevState.display
                    });
                }

                // if there's more than one number
            }else{
                const newLastNumber = '-' + lastItem; // add '-' in front of the last number

                //**
                // this is to double-check if the two symbols in front of the last number are not eg. 2 - -2
                // if they are the second '-' needs to be removed to make the second 2 a positive number
                //**

                // if the char before the last number and the character before it are not numbers
                // and if the character before the character in front of the number isn't a '%'
                if( isNaN(beforeLast) && isNaN(beforeBeforeLast) && beforeBeforeLast != '%' ){

                    // remove the first character before the last number
                    return ({
                        calculate: prevState.calculate.slice(0, calcLength - lastItemLength - 1) + lastItem,
                        display: prevState.display.slice(0, calcLength - lastItemLength - 1) + lastItem
                    });
                }else{
                    // replace the last number with the new last number (with the '-')
                    return ({
                        calculate: prevState.calculate.slice(0, calcLength - lastItemLength) + newLastNumber,
                        display: prevState.display.slice(0, calcLength - lastItemLength) + newLastNumber
                    });
                }

            }

        });
    }


    clearCalculate(e){
        this.setState(() => ({ 
            display: '0',
            calculate: '0',
        }));

        this.clearEquals();
    }

    sumCalculate(e){
        let result = '';
        result = evaluate(this.state.calculate);

        if(result != null){
            this.setState(() => ({ 
                display: result,
                equals: true
            }));
        }

    }

    clearEquals(){
        this.setState(() => ({ 
            equals: false
        }));
    }

    getDisplayValue(e) {

        let displayValue = '';

        if(e.target.getAttribute('display') !== null){
            displayValue = e.target.getAttribute('display');
        }else{
            displayValue = e.target.value;
        }

        return displayValue;

    }


    render(){
        return(  
            <div className={'ui grid calculator'}>
                <div className={ 'row results-row '}>
                    <span>{ this.state.display }</span>
                </div>
                <div className={ 'row'}>
                    <div className={"twelve wide column"}>
                        <div className={'ui grid'}>
                            <div className={'three column row'}>
                                <div className={'column'}>
                                    <button onClick={this.clearCalculate} className={'ui button fluid'}>C</button>
                                </div>

                                <div className={'column'}>
                                    <button onClick={this.changeCharge} className={'ui button fluid'}>+/-</button>
                                </div>

                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='%' className={'ui button fluid'}>%</button>
                                </div>

                            </div>
                            <div className={'three column row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='1' className={'ui button fluid'}>1</button>
                                </div>

                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='2' className={'ui button fluid'}>2</button>
                                </div>

                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='3' className={'ui button fluid'}>3</button>
                                </div>
                            </div>

                            <div className={'three column row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='4' className={'ui button fluid'}>4</button>
                                </div>

                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='5' className={'ui button fluid'}>5</button>
                                </div>

                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='6' className={'ui button fluid'}>6</button>
                                </div>
                            </div>
                            <div className={'three column row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='7' className={'ui button fluid'}>7</button>
                                </div>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='8' className={'ui button fluid'}>8</button>
                                </div>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='9' className={'ui button fluid'}>9</button>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className={"four wide column"}>
                        <div className={'ui grid'}>
                            <div className={'row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='+' className={'ui button fluid teal'}>+</button>
                                </div>
                            </div>
                            <div className={'row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='-' className={'ui button fluid teal'}>-</button>
                                </div>
                            </div>
                            <div className={'row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='*' display='x' className={'ui button fluid teal'}>x</button>
                                    </div>
                            </div>
                            <div className={'row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='/' className={'ui button fluid teal'}>/</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                <div className={ 'row'}>
                    <div className={'four wide column'}>
                        <button onClick={this.changeCalculate} value='0' className={'ui button fluid'}>0</button>
                    </div>
                    <div className={'four wide column'}>
                        <button onClick={this.changeCalculate} value='.' className={'ui button fluid'}>.</button>
                    </div>
                    <div className={'eight wide column'}>
                        <button className={'ui button fluid grey'} onClick={this.sumCalculate} >=</button>
                    </div>

                </div>
            </div>

        );
    }
}
