import React from "react";
import { evaluate } from 'mathjs';

export class Calculator extends React.Component{

    constructor(props){
        super(props);

        this.state = {
            calculate : '0',
            display: '0',
            equals: false
        };

        this.changeCalculate = this.changeCalculate.bind(this);
        this.sumCalculate = this.sumCalculate.bind(this);
        this.clearCalculate = this.clearCalculate.bind(this);
        this.changeCharge = this.changeCharge.bind(this);

    }


    changeCalculate(e){

        // if this input is not after an equals sign
        this.setState((prevState) => {
            let newDisplayValue = this.getDisplayValue(e);
            const newCalcValue = e.target.value;

            const calcCount = prevState.calculate.length; // calculate state count
            const lastLetter = prevState.calculate.slice(-1); // last letter of the calc state

            const descCount = prevState.display.length; // display state count

            // if equals was the last button clicked
            if(this.state.equals == false){

                // if it's the first input and the number is bigger than zero
                // if both previous and new input are not numbers
                if( calcCount == 1 && prevState.calculate == '0' && parseFloat(e.target.value) > 0 || isNaN(parseFloat(lastLetter)) && isNaN((parseFloat(newCalcValue)))) {

                    // replace the previous input
                    return ({ 
                        calculate: prevState.calculate.slice(0, calcCount -1) + newCalcValue,
                        display: prevState.display.slice(0, descCount -1) + newDisplayValue,
                    });

                }else{

                    // don't replace
                    return ({ 
                        calculate: prevState.calculate + newCalcValue,
                        display: prevState.display + newDisplayValue,
                    });
                }

            }else{            
                // if new value is not a number
                if(isNaN(newCalcValue)){

                    // add to previous values
                    return ({ 
                        calculate: prevState.calculate + newCalcValue,
                        display: prevState.display + newDisplayValue,
                    });
                }else{

                    // replace the previous value entirely
                    return ({ 
                        calculate: newCalcValue,
                        display: newDisplayValue
                    });
                }
            }

        });

        this.clearEquals();
    }

    changeCharge(e){
        this.setState((prevState) => {
            // const lastLetter = prevState.calculate.slice(-3); // last letter of the calc state

            const calcCount = prevState.calculate.length; // calculate state count
            const descCount = prevState.display.length; // display state count


            const splitString = prevState.calculate.match(/(?:\d+\.)?\d+/g); // split by numbers
            const lastItem = splitString[splitString.length - 1]; // get the last number
            const letterCount = lastItem.length; // char count of the last number
            const lastSymbol = prevState.calculate.charAt(-3);

            console.log(lastItem);
            // console.log(letterCount);
            console.log(lastSymbol);

            // if there's only one number
            // if( calcCount == 1 ){

            //     if(!isNaN(prevState.calculate)){
            //         return ({
            //             calculate: '-' + prevState.calculate,
            //             display: '-' + prevState.display
            //         });
            //     }

            // } else if(calcCount == 2){
            //     console.log('calcount is 2');

            //     if(lastLetter.charAt(0) == '-' && !isNaN(lastLetter.charAt(1))){
            //         // remove the minus

            //         return ({
            //             calculate: lastLetter.charAt(1),
            //             display: lastLetter.charAt(1)
            //         });

            //     }

            // }else if(calcCount >= 3){

            //     if(lastLetter.charAt(0) == '-' && lastLetter.charAt(1) == '-'){
            //         console.log('hello');
            //     }else{
            //         return ({
            //             calculate: prevState.calculate.slice(0, calcCount -1) + '-' + lastLetter.charAt(2),
            //             display: prevState.display.slice(0, descCount -1) + '-' + lastLetter.charAt(2),
            //         });
            //     }
            // }


        });
    }

    // calculatecharas(haystack, needle){
    //     let count = 0;

    //     for(let i = 0; i<haystack.length; i++){
    //         if(haystack.charAt(i) == needle){
    //           count ++;
    //         }
    //     }

    //     return count;
    // }


    clearCalculate(e){
        this.setState(() => ({ 
            display: '0',
            calculate: '0',
        }));

        this.clearEquals();
    }

    sumCalculate(e){
        let result = '';
        result = evaluate(this.state.calculate);

        if(result != null){
            this.setState(() => ({ 
                display: result,
                equals: true
            }));
        }

    }

    clearEquals(){
        this.setState(() => ({ 
            equals: false
        }));
    }

    getDisplayValue(e) {

        let displayValue = '';

        if(e.target.getAttribute('display') !== null){
            displayValue = e.target.getAttribute('display');
        }else{
            displayValue = e.target.value;
        }

        return displayValue;

    }


    render(){
        return(  
            <div className={'ui grid calculator'}>
                <div className={ 'row results-row '}>
                    <span>{ this.state.display }</span>
                </div>
                <div className={ 'row'}>
                    <div className={"twelve wide column"}>
                        <div className={'ui grid'}>
                            <div className={'three column row'}>
                                <div className={'column'}>
                                    <button onClick={this.clearCalculate} className={'ui button fluid'}>C</button>
                                </div>

                                <div className={'column'}>
                                    <button onClick={this.changeCharge} className={'ui button fluid'}>+/-</button>
                                </div>

                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='%' className={'ui button fluid'}>%</button>
                                </div>

                            </div>
                            <div className={'three column row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='1' className={'ui button fluid'}>1</button>
                                </div>

                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='2' className={'ui button fluid'}>2</button>
                                </div>

                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='3' className={'ui button fluid'}>3</button>
                                </div>
                            </div>

                            <div className={'three column row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='4' className={'ui button fluid'}>4</button>
                                </div>

                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='5' className={'ui button fluid'}>5</button>
                                </div>

                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='6' className={'ui button fluid'}>6</button>
                                </div>
                            </div>
                            <div className={'three column row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='7' className={'ui button fluid'}>7</button>
                                </div>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='8' className={'ui button fluid'}>8</button>
                                </div>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='9' className={'ui button fluid'}>9</button>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className={"four wide column"}>
                        <div className={'ui grid'}>
                            <div className={'row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='+' className={'ui button fluid teal'}>+</button>
                                </div>
                            </div>
                            <div className={'row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='-' className={'ui button fluid teal'}>-</button>
                                </div>
                            </div>
                            <div className={'row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='*' display='x' className={'ui button fluid teal'}>x</button>
                                    </div>
                            </div>
                            <div className={'row'}>
                                <div className={'column'}>
                                    <button onClick={this.changeCalculate} value='/' className={'ui button fluid teal'}>/</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                <div className={ 'row'}>
                    <div className={'four wide column'}>
                        <button onClick={this.changeCalculate} value='0' className={'ui button fluid'}>0</button>
                    </div>
                    <div className={'four wide column'}>
                        <button onClick={this.changeCalculate} value='.' className={'ui button fluid'}>.</button>
                    </div>
                    <div className={'eight wide column'}>
                        <button className={'ui button fluid grey'} onClick={this.sumCalculate} >=</button>
                    </div>

                </div>
            </div>

        );
    }
}
